import React from 'react'
import { useState, useEffect } from 'react'
import { useNavigate, useLocation, useParams, Link } from 'react-router-dom'
import PostMessage from '../components/PostMessage'
import GuestMessage from '../components/GuestMessage'
import '../style/App.css'
import axios from 'axios'

const Message = () => {
  const location = useLocation()
  let navigate = useNavigate()
  const { msgId } = useParams()

  const BASE_URL = 'http://localhost:3001/api'
  //display all messages ==> getMessage()
  const [allMessage, setAllMessage] = useState([])

  //get message by Id ==> getMessageById
  let [specificMessage, setSpecificMessage] = useState('')

  //update message ==> updateMessage()
  const [messageUpdate, setMessageUpdate] = useState('')

  //delete message ==> deletemessage
  let [messageDelete, setMessageDelete] = useState({})

  useEffect(() => {
    let getMessage = async () => {
      let response = await axios.get(`${BASE_URL}/message`)
      setAllMessage(response.data.msg)
    }
    getMessage()
  }, [])

  const deleteMessage = async (msgId) => {
    const response = await axios
      .delete(`${BASE_URL}/message/${msgId}`)
      .then(function (res) {
        console.log(res, 'DELETE MESSAGE')
      })
      .catch(function (error) {
        console.log(error)
      })
  }

  const data = {
    msg: messageUpdate
  }
  let updateMessage = async (msgId) => {
    console.log(msgId, 'MSG ID')
    await axios
      .put(`${BASE_URL}/message/${msgId}`, data)
      .then(function (res) {
        setMessageUpdate(res.data)
        console.log(res, 'RESPONSE FOR UPDATE')
        console.log(res.data, 'UPDATE MESSAGE')
      })
      .catch(function (error) {
        console.log(error)
      })
  }

  const handleChange = (e) => {
    e.preventDefault()
    setMessageUpdate(e.target.value)
  }

  return (
    <div>
      <h2>Leave a message for the couple!</h2>
      <div className="msgContainer">
        <div className="msgDisplay">
          {allMessage.map((msg) => (
            <div>
              <img src={msg.image} />
              <h3>{msg.msg}</h3>
              <button
                onClick={() => {
                  deleteMessage(msg._id)
                }}
              >
                Delete Message
              </button>
              <div>
                <input onChange={handleChange} value={messageUpdate}></input>
                <button
                  onClick={() => {
                    updateMessage(msg._id)
                  }}
                >
                  Update Message
                </button>
              </div>
            </div>
          ))}
          <GuestMessage />
        </div>
      </div>
    </div>
  )
}

export default Message
