import React from 'react'
import { useState, useEffect } from 'react'
import PostMessage from '../components/PostMessage'
import GuestMessage from '../components/GuestMessage'
import '../style/App.css'
import axios from 'axios'

const Message = () => {
  const BASE_URL = 'http://localhost:3001/api'
  const [postMessage, setPostMessage] = useState([])
  const [message, setMessage] = useState('')
  const [selected, setSelected] = useState(false)

  const getMessage = async () => {
    await axios
      .post(`${BASE_URL}/message`)
      .then(function (response) {
        console.log(response, 'POST MESSAGE')
        setPostMessage(response.data)
      })
      .catch(function (error) {
        console.log(error)
      })
  }
  getMessage()

  const handleChange = (e) => {
    e.preventDefault()
    setMessage(e.target.value)
  }

  const addMessage = () => {
    let myMessage = [...postMessage, message]
    setPostMessage(myMessage)
    setMessage('')
  }

  const removeMessage = (message) => {
    let addedMessage = [...postMessage]
    addedMessage.splice(message, 1)
    setPostMessage(addedMessage)
  }

  // const displayMessage = () => {
  //   const data = {
  //     message: message
  //   }
  // }

  return (
    <div>
      <h2>Leave a message for the couple!</h2>
      <div>
        <GuestMessage
          handleChange={handleChange}
          addMessage={addMessage}
          message={message}
        />
        <PostMessage postMessage={postMessage} removeMessage={removeMessage} />
      </div>
    </div>
  )
}

export default Message
